// Generates the vector x and stores it in the memory
void generateVector(float x[N])
{
	int i;
	x[0] = 0;
	for (i=1; i<N; i++)
		x[i] = x[i-1] + step;
}

float sumVector(float x[], int M)
{
 float y = 0;
 int j;
 for(j=0;j<M;j++){
	 y = y + x[j] * 0.5 + x[j] * x[j] * cosf((x[j]-128)/128);}
 return y;
}

float sumVector_custom(float x[], int M)
{
 float y = 0;
 int j;
 for(j=0;j<M;j++){
	 float sub_custom = ALT_CI_MOD_CUSTOM_TOP_0(0,x[j],128);
	 float product = x[j]*cosf(sub_custom/128);
	 y = y + ALT_CI_MOD_CUSTOM_TOP_0(1,x[j],0.5+product);}
	 //y = y + x[j]*0.5 + ALT_CI_MOD_FP_MULT_1(ALT_CI_MOD_FP_MULT_1(x[j],x[j]),cosf((x[j]-128)/128));}
 return y;
}

